import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [visibleProducts, setVisibleProducts] = useState(6); // Initial number of products to display
  const [searchTerm, setSearchTerm] = useState('');
  const [filters, setFilters] = useState({
    size: '',
    color: '',
    brand: ''
  });

  useEffect(() => {
    axios.get('https://fakestoreapi.com/products')
      .then(response => {
        setProducts(response.data);
        setLoading(false);
      })
      .catch(error => {
        setError(error);
        setLoading(false);
      });
  }, []);

  const filteredProducts = products.filter(product =>
    product.title.toLowerCase().includes(searchTerm.toLowerCase()) &&
    (filters.size ? product.size === filters.size : true) &&
    (filters.color ? product.color === filters.color : true) &&
    (filters.brand ? product.brand === filters.brand : true)
  );

  const loadMore = () => {
    setVisibleProducts(prevVisibleProducts => prevVisibleProducts + 6); // Load 6 more products
  };

  const handleFilterChange = (filterType, value) => {
    setFilters(prevFilters => ({
      ...prevFilters,
      [filterType]: value
    }));
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error loading products.</p>;

  return (
    <div>
      {/* Product list */}
      <div className="row">
        {filteredProducts.slice(0, visibleProducts).map(product => (
          <div className="col-md-4" key={product.id}>
            <div className="card mb-4">
              <img className="card-img-top" src={product.image} alt={product.title} />
              <div className="card-body">
                <h5 className="card-title">{product.title}</h5>
                <p className="card-text">${product.price}</p>
                <a href="#" className="btn btn-primary">View Details</a>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Load more button */}
      {visibleProducts < filteredProducts.length && (
        <div className="text-center">
          <button className="btn btn-primary" onClick={loadMore}>Load More</button>
        </div>
      )}

      {/* Search bar */}
      <div className="mt-3">
        <input
          type="text"
          placeholder="Search products..."
          value={searchTerm}
          onChange={e => setSearchTerm(e.target.value)}
        />
      </div>

      {/* Filters */}
      <div className="mt-3">
        <select value={filters.size} onChange={e => handleFilterChange('size', e.target.value)}>
          <option value="">Select Size</option>
          {/* Add size options here */}
        </select>
        {/* Add filters for color, brand, etc. */}
      </div>
    </div>
  );
};

export default ProductList;
